import Stack from '@material-ui/core/Stack';
import Box from '@material-ui/core/Box';
import { styled } from '@material-ui/core/styles';
import useWindowDimensions from '../../components/ui/Window.js';
import theme from "../../components/ui/Theme.js";
import { useState, useEffect } from "react";
import { Parallax } from 'react-parallax';

const brot1 = '../../resource/images/brot1.png';
const brot2 = '../../resource/images/brot2.png';
const brot3 = '../../resource/images/brot3.png';
const brot4 = '../../resource/images/brot4.png';
const brot5 = '../../resource/images/brot5.png';

const ab = '../../resource/images/ab.png';
const a1 = '../../resource/images/a1.png';
const a2 = '../../resource/images/a2.png';
const a3 = '../../resource/images/a3.png';

const SectionBox = styled(Box)(({ theme }) => ({
    width: '100%',
    height: '${ props => props.height ? props.height : window.innerHeight}',
    marginTop: '${ props => props.height ? props.height / 8 : window.innerHeight / 8 }',
    backgroundSize: 'auto 100%',
    backgroundPosition: 'center'
}));

function SectionBG(props) {
    return (
        <div />
    );
}

function MainPage() {
    const height = useWindowDimensions().height * 1.5;

    const [offsetY, setOffsetY] = useState(0);
    const handleScroll = () => setOffsetY(window.pageYOffset);

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    return (
        <div>
            <Stack spacing={0} style={{ transform: `translateY(${offsetY * -0.5}px)` }}>

                <SectionBox height={height} style={{ backgroundImage: `url(${brot1})` }}>
                    ASDF
                </SectionBox>

                <SectionBox height={height} style={{ backgroundImage: `url(${brot1})` }}>
                    ASDF
                </SectionBox>

                <SectionBox height={height} style={{ backgroundImage: `url(${brot1})` }}>
                    ASDF
                </SectionBox>

                <SectionBox height={height} style={{ backgroundImage: `url(${brot1})` }}>
                    ASDF
                </SectionBox>

                <SectionBox height={height} style={{ backgroundImage: `url(${brot1})` }}>
                    ASDF
                </SectionBox>
            </Stack>
        </div>

    )
}

export default MainPage;
