import Stack from '@material-ui/core/Stack';
import Box from '@material-ui/core/Box';
import { styled } from '@material-ui/core/styles';
import useWindowDimensions from '../../components/ui/Window.js';
import theme from "../../components/ui/Theme.js";
import { useState, useEffect } from "react";
import { Parallax } from 'react-parallax';

import brot1 from '../../resource/images/brot1.png';
import brot2 from '../../resource/images/brot2.png';
import brot3 from '../../resource/images/brot3.png';
import brot4 from '../../resource/images/brot4.png';
import brot5 from '../../resource/images/brot5.png';

import ab from '../../resource/images/ab.png';
import a1 from '../../resource/images/a1.png';
import a2 from '../../resource/images/a2.png';
import a3 from '../../resource/images/a3.png';

const SectionBox = styled(Box)(({ theme }) => ({
    width: '100%',
    backgroundColor: theme.palette.success.main,
    backgroundImage: brot5
}));

function Section(props) {
    return (
        <SectionBox style={{ height: props.height }} sx={{ mt: props.height / 8 }}>
        </SectionBox>
    );
}

function MainPage() {
    const height = useWindowDimensions().height * 1.5;

    const [offsetY, setOffsetY] = useState(0);
    const handleScroll = () => setOffsetY(window.pageYOffset);

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    return (
        <div>
            <Stack spacing={height / 8} style={{ transform: `translateY(${offsetY * -0.5}px)` }}>
                <Section height={height}/>
                <Section height={height}/>
                <Section height={height}/>
                <Section height={height}/>
                <Section height={height}/>
            </Stack>
        </div>

    )
}

export default MainPage;
