import Stack from '@material-ui/core/Stack';
import Box from '@material-ui/core/Box';
import { styled } from '@material-ui/core/styles';
import useWindowDimensions from '../../components/ui/Window.js';
import theme from "../../components/ui/Theme.js";
import { useState, useEffect } from "react";

const Section = styled(Box)(({ theme }) => ({
    width: '100%',
    backgroundColor: theme.palette.success.main
}));

function MainPage() {
    const height = useWindowDimensions().height * 1.5;

    const [offsetY, setOffsetY] = useState(0);
    const handleScroll = () => setOffsetY(window.pageYOffset);

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    console.log(offsetY);

    return (
        <div>
            <div className="parallax1" style={{ transform: `translateY(${offsetY * 0.5}px)` }}> AAAAA </div>
            <Stack spacing={height / 8}>
                <Section style={{ height: height }} sx={{ mt: height/8}}> ASDF </Section>
                <Section style={{ height: height }} />
                <Section style={{ height: height }} />
                <Section style={{ height: height }} />
                <Section style={{ height: height }} />
            </Stack>
        </div>

    )
}

export default MainPage;
